	[Dormitório de professor(a)],
	[Estúdio de Gravação e Edição],	
    [Laboratório de ciências], 	
    [Laboratório de informática], 	
    [Laboratório especifico para educação profissional]
    [Parque infantil], 	
    [Pátio coberto],	
    [Pátio descoberto], 	
    Piscina, 	
    [Quadra de esportes coberta], 	
    [Quadra de esportes descoberta], 	
    Refeitório, 	
	[Sala de diretoria],	
    [Sala de leitura],	
    [Sala de música/coral],	
    [Salas de oficinas da educação profissional],
    [Sala de professores],	
    [Sala de recursos multifuncionais para atendimento educacional especializado],	
    [Sala de repouso para aluno(a)],
    [Sala de secretaria],
    [Sala mulituso (música, dança e artes)],	
    [Antena parabólica], 	
