	Copiadora,
	Impressora, 	
	[Impressora multifuncional], 	
	Scanner, 	
	[Lousa digital],	
	[Aparelho de DVD/Blu-ray], 	
	[Aparelho de som], 	
	[Projetor multimídia (Data show)],
	[Aparelho de televisão] 

	INTO ##escola_relatorio_SP_UF_A07_CENSO
	
	FROM SME_ESCOLA_DIARIO A
	JOIN (SELECT
          CD_ESCOLA,
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '20'         THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS 'Almoxarifado',
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '10016'      THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS  'Área de Horta, Plantio e/ou Produção Agrícola',
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '10003'      THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Área de Vegetação ou Gramado',	
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '23'         THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Auditório', 	
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA IN ('37','38') THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Banheiro', 	
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '115  '      THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Banheiro acessível, adequado ao uso de pessoas com deficiência ou mobilidade reduzida',	
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '148  '      THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Banheiro adequado à educação infantil',	
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA IN ('38','39') THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Banheiro exclusivo para os funcionários',	
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '10002'      THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Banheiro ou vestiário com chuveiro',
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '135'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Biblioteca', 	
          COUNT(DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '33'         THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS   'Cozinha', 	
          '' AS 'Despensa', 	
          '' AS 'Dormitório de aluno(a)', 	
          '' AS 'Dormitório de professor(a)', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '10008'   THEN CD_AMBIENTE_ESCOLA  ELSE '' END) AS    'Estúdio de Gravação e Edição',	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '25'      THEN CD_AMBIENTE_ESCOLA  ELSE '' END) AS    'Laboratório de ciências', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '103'     THEN CD_AMBIENTE_ESCOLA  ELSE '' END) AS    'Laboratório de informática', 	
          '' AS 'Laboratório especifico para educação profissional', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '55'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Parque infantil', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '46'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Pátio coberto',	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '47'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Pátio descoberto', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '56'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Piscina', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '44'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Quadra de esportes coberta', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '45'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Quadra de esportes descoberta', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '34'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Refeitório', 	
	      COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '14'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Sala de diretoria',	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '9'         THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Sala de leitura',	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '10004'     THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS    'Sala de música/coral',	
          '' AS 'Salas de oficinas da educação profissional',	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '17'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Sala de professores',	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '10000'     THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS    'Sala de recursos multifuncionais para atendimento educacional especializado',	
          '' AS 'Sala de repouso para aluno(a)', 	
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '18'        THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS      'Sala de secretaria',
          COUNT (DISTINCT CASE WHEN CD_AMBIENTE_ESCOLA = '10005'     THEN CD_AMBIENTE_ESCOLA ELSE '' END) AS    'Sala mulituso (música, dança e artes)'
          FROM D_AMBIENTE_ESCOLA
	      WHERE DT_FIM IS NULL
	      GROUP BY cd_escola) B ON A.cd_escola=B.CD_ESCOLA
	JOIN (SELECT 
           CD_ESCOLA,			   	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola IN ('11','20') THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS  'Antena parabólica', 	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola IN ('29','30')THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Computadores', 	
           '' AS 'Copiadora', 												
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola IN('12','15') THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Impressora', 	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola='31'          THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Impressora multifuncional', 	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola= '32'         THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Scanner', 	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola='33'          THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Lousa digital',	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola='28'          THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Aparelho de DVD/Blu-ray', 	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola='25'          THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Aparelho de som', 	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola='18'          THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Projetor multimídia (Data show)',	
           COUNT(DISTINCT CASE WHEN cd_tipo_equipamento_escola='19'          THEN CD_EQUIPAMENTO_ESCOLA ELSE '' END) AS   'Aparelho de televisão' 
          FROM [DB_EDUCACAO_1_PRODAM].[se1426_ss].[dbo].[equipamento_escola]
		  WHERE DT_FIM IS NULL
          GROUP BY CD_ESCOLA)  C ON A.CD_ESCOLA=C.CD_ESCOLA
				   
